const text = $input.first().json.data
const chunkSize = 500;
const chunkOverlap = 50;
const chunks = [];


for (let i = 0; i < text.length; i += chunkSize - chunkOverlap) {
    const chunk = text.substring(i, i + chunkSize);
    chunks.push({ json: { chunk: chunk } });
}

return chunks;
